import React, { useMemo } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import axios from 'axios';
import Search from '../../Search/index';
import SearchIcon from '@material-ui/icons/Search';


import { useState, useEffect } from 'react'


const useStyles = makeStyles({

    root: {
        width: '115%',
        marginLeft: '-82px',
        marginTop: '-40px',
        backgroundColor: "#f2ecec",

        borderTop: 'outset',
    },

    container: {

        borderRightColor: 'red',
    },
});



export default function MatPaginationTable() {
    const [comments, setComments] = useState([]);
    const classes = useStyles();
    const [page, setPage] = useState(0);
    const [data, setData] = useState([]);
    const [rowsPerPage, setRowsPerPage] = React.useState(5);
    const [totalItems, setTotalItems] = useState(0);
    const [currentPage, setCurrentPage] = useState(1);
    const [search, setSearch] = useState("");
    const [sorting, setSorting] = useState({ field: "", order: "" });
    const [test, setTest] = useState(false);

    const ITEMS_PER_PAGE = 10;

    useEffect(() => {
        setTimeout(() => {
            getData();
        }, 10);
    }, [test]);
    const getData = () => {


        fetch("https://entemadb.entema-software.com/getBookingData")
            .then((response) => response.json())
            .then((json) => {

                setComments(json);
                console.log('herooo  ', comments);
                // console.log(comments[0].BOOK_FNAME)
            });
    };

    const removeDelTimeSheet = (id) => {
        axios
            .post("https://entemadb.entema-software.com/removeDelNoteBasedOnId", {
                delId: id,
            })
            .then((res) => {
                getData();

            });
    };

    const commentsData = useMemo(() => {
        let computedComments = comments;

        if (search) {
            computedComments = computedComments.filter(
                (comment) =>
                    comment.BOOK_FNAME.toLowerCase().includes(search.toLowerCase()) ||
                    comment.BOOK_EMAIL.toLowerCase().includes(search.toLowerCase()) ||
                    comment.BOOK_PHONE.toLowerCase().includes(search.toLowerCase())
            );
        }
        setTotalItems(computedComments.length);

        //Sorting comments
        if (sorting.field) {
            const reversed = sorting.order === "asc" ? 1 : -1;
            computedComments = computedComments.sort(
                (a, b) => reversed * a[sorting.field].localeCompare(b[sorting.field])
            );
        }

        //Current Page slice
        return computedComments.slice(
            (currentPage - 1) * ITEMS_PER_PAGE,
            (currentPage - 1) * ITEMS_PER_PAGE + ITEMS_PER_PAGE
        );
    }, [comments, currentPage, search, sorting]);

    useEffect(() => {
        getData();
    }, [test]);

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = event => {
        setRowsPerPage(+event.target.value);

        setPage(0);

    };





    return (<>
         <div style={{
                marginTop: "15px", marginLeft: "870px"
            }}>
                <Search

                    onSearch={(value) => {
                        setSearch(value);
                        // setCurrentPage(1);
                    }}
                >

                </Search>
            </div>
           

        <div className="container" style={{ color: 'milkywhite' }}>
            
               <text style={{fontWeight:"bolder", fontSize:"50px"}}
               >Room Reservations</text>

           

            <section className="featured-rooms">


                <Paper className={classes.root}>

                    <TableContainer className={classes.container}>
                        <Table stickyHeader aria-label="sticky table">
                            <TableHead>
                                <TableRow>

                                    <TableCell align="center" style={{ backgroundColor: "#e8e5f6" }}>Room No</TableCell>
                                    <TableCell align="center" style={{ backgroundColor: "#e8e5f6" }}>Room Type</TableCell>
                                    <TableCell align="center" style={{ backgroundColor: "#e8e5f6" }}>FUll Name</TableCell>
                                    <TableCell align="center" style={{ backgroundColor: "#e8e5f6" }}>Email ID</TableCell>
                                    <TableCell align="center" style={{ backgroundColor: "#e8e5f6" }}>Phone Number</TableCell>
                                    <TableCell align="center" style={{ backgroundColor: "#e8e5f6" }}>Check In</TableCell>
                                    <TableCell align="center" style={{ backgroundColor: "#e8e5f6" }}>Check Out</TableCell>
                                    <TableCell align="center" style={{ backgroundColor: "#e8e5f6" }}>Action</TableCell>

                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {commentsData.map((comments) => (
                                    <TableRow >
                                        <TableCell align="center">01</TableCell>

                                        <TableCell align="center"> {comments.BOOK_ROOM_TYPE}</TableCell>
                                        <TableCell align="center">{comments.BOOK_FNAME}</TableCell>
                                        <TableCell align="center">{comments.BOOK_EMAIL}</TableCell>
                                        <TableCell align="center">{comments.BOOK_PHONE}</TableCell>
                                        <TableCell align="center">{comments.BOOK_CHECKIN}</TableCell>
                                        <TableCell align="center">{comments.BOOK_CHECKOUT}</TableCell>
                                        <TableCell align="center"><button type="submit" className="btn-primary" style={{ width: '83px' }}> Check Out</button></TableCell>                        </TableRow>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                    <TablePagination
                        rowsPerPageOptions={[5, 10, 25, 50]}
                        component="div"
                        count={data.length}
                        rowsPerPage={rowsPerPage}
                        page={page}
                        onChangePage={handleChangePage}
                        onChangeRowsPerPage={handleChangeRowsPerPage}
                    />
                </Paper> </section></div>
    </>
    );
}
